import logo from '../images/header-logo.svg';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import React, {useState} from 'react';
import { Route, Routes } from 'react-router-dom';

const Header = ({userEmail, handleLogOut, loggedIn}) => {
    const location = useLocation();

    const [nav, setNav] = useState(false);





    return (
      <header className="header">
        <img className="header__logo" src={logo} alt="Логотип сайта" />
        <Routes>
          <Route
          path="/sign-in"
          element={
          <Link to={location.pathname === '/sign-in' ? '/sign-up' : '/sign-in'}
        className="header__button">{location.pathname === '/sign-in' ? 'Регистрация' : 'Вход'}
        </Link>
        } />

          <Route path="/">
            <nav className="header__nav">
              <p className="header__nav-email">{userEmail}</p>
              <button className="header__nav-exit">Выход</button>
            </nav>
          </Route>
        </Routes>


      </header>
    );



