import logo from '../images/header-logo.svg';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import React, {useState, useEffect} from 'react';

const Header = ({loggedIn, handleLogOut, userEmail, handleBurgerClick}) => {
    const location = useLocation();

    const [isBurgerMenuOpen, setIsBurgerMenuOpen] = useState(false);

    useEffect(() => {
      setIsBurgerMenuOpen(false)
    }, [])



    const HeaderLink = () => {
      return (
       <Link
       to={location.pathname === '/sign-in' ? '/sign-up' : '/sign-in'}
       className="header__button">{location.pathname === '/sign-in' ? 'Регистрация' : 'Вход'}</Link>
      )
    }

    const HeaderNav = () => {
      return (
      <nav className="header__nav">
      <p className="header__nav-email">{userEmail}</p>
       <button className="header__nav-exit" onClick={handleLogOut}>Выйти</button>
      </nav>
      )
    }

    return (
      <header className="header">
        <img className="header__logo" src={logo} alt="Логотип сайта" />
        { loggedIn ? <HeaderNav/> : <HeaderLink/> }
      </header>
    );
};
export default Header;
