import logo from '../images/header-logo.svg';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import React from 'react';

const Header = ({loggedIn, handleLogOut, userEmail, isBurgerMenuOpen, handleBurgerClick}) => {
    const location = useLocation();

    const HeaderLink = () => {
      return (
       <Link
       to={location.pathname === '/sign-in' ? '/sign-up' : '/sign-in'}
       className="header__button">{location.pathname === '/sign-in' ? 'Регистрация' : 'Вход'}
       </Link>
      )
    }

    const HeaderNav = () => {
      return (
      <nav className="header__nav">
      <p className="header__nav-email">{userEmail}</p>
       <button className="header__nav-exit" onClick={handleLogOut}>Выйти</button>
      </nav>
      )
    }

    const BurgerMenu = ({ userEmail, handleLogOut, isBurgerMenuOpen, loggedIn }) => {
      return (
        <div className={`burger-menu ${isBurgerMenuOpen && "burger-menu_opened"} `}>

          <p className="burger-menu__email">{userEmail}</p>
          <button className="burger-menu__nav-exit" type="button" onClick={handleLogOut}>Выйти</button>

      </div>
      )
    }

    return (
      <div>
        <BurgerMenu
        userEmail={userEmail}
        handleLogOut={handleLogOut}
        isBurgerMenuOpen={isBurgerMenuOpen}
        loggedIn={loggedIn}
        />
        <header className="header">
        <img className="header__logo" src={logo} alt="Логотип сайта" />
        { loggedIn ? <HeaderNav/> : <HeaderLink/> }
        <button
        className={`burger-menu__button ${isBurgerMenuOpen && 'burger-menu__button_active'}`}
        type="button"
        onClick={handleBurgerClick}></button>
      </header>
      </div>
    );
};
export default Header;
