import React, {useState, useEffect} from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import AddPlacePopup from './AddPlacePopup';
import ConfirmDeletePopup from './ConfirmDeletePopup';
import ImagePopup from './ImagePopup';
import avatarPreloader from '../images/avatarka.jpg';
import api from '../utils/Api.js';
import { Route, Routes, useNavigate } from 'react-router-dom';
import ProtectedRoute from './ProtectedRoute';
import Login from './Login';
import Register from './Register';
import * as auth from '../utils/auth';

import { CurrentUserContext } from '../contexts/CurrentUserContext';

const App = () => {

  const navigate = useNavigate();
  //states variables of components
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isConfirmPopupOpen, setIsConfirmPopupOpen] = useState(false);

  const [loggedIn, setLoggedIn] = useState(false);
  const [userData, setUserData] = useState({
    email: '',
    password: '',
  })

  const [selectedCard, setSelectedCard] = useState(null);
  const [cardToDelete, setCardToDelete] = useState([]);
  const [cards, setCards] = useState([]);

  const [currentUser, setCurrentUser] = React.useState({
    name: 'Загрузка',
    about: '...',
    avatar: avatarPreloader,
  });

  //get user information and inital cards from the server
  useEffect(() => {
    Promise.all([api.getUserInfo(), api.getInitialCards()])
      .then(([userData, userCard]) => {
        setCurrentUser(userData);
        setCards(userCard);
      })
      .catch((err) => console.log(`Ошибка: ${err}`));
  }, []);


  // useEffect(() => {
  //   const jwt = localStorage.getItem('jwt');
  //   if(jwt) {
  //     auth.checkToken(jwt)
  //     .then((data) => {
  //       if(data) {
  //         setUserData({
  //           email: data.data.email,
  //         });
  //         setLoggedIn(true);
  //         navigate('/', {replace: true});
  //       }
  //     })
  //     .catch((err) => console.log(err));
  //   }
  // }, [navigate])

  useEffect(() => {
    check()
  }, [])


  const check = () => {
    const jwt = localStorage.getItem('jwt');
    if(jwt) {
      auth.checkToken(jwt)
      .then((res) => {
        if(res.jwt) {
          localStorage.setItem('jwt', res.jwt)
          console.log(res)
          setUserData({
            email: res.data.email
          });
          setLoggedIn(true);
          navigate('/');
        }
      })
    }
  }


  const handleRegister = (email, password) => {
    auth.register(email, password)
    .then((data) => {
      if(data.jwt) {
        localStorage.setItem('jwt', data.jwt)
        setUserData({
          email: data.data.email
        })
        navigate('/sign-in');
      }
    })
    .catch((err) => console.log(err));
  }


  const handleLogin = (email, password) => {
    auth.login(email, password)
    .then((res) => {
      if(res.jwt) {
        setUserData({
          email: res.data.email
        });
        setLoggedIn(true);
        localStorage.setItem('jwt', res.jwt);
        navigate('/', {replace: true});
      }

    })
    .catch((err) => console.log(err));
  }


  //functions for openning popups
  const handleEditProfileClick = () => {
    setEditProfilePopupOpen(true);
  };

  const handleAddPlaceClick = () => {
    setIsAddPlacePopupOpen(true);
  };

  const handleEditAvatarClick = () => {
    setIsEditAvatarPopupOpen(true);
  };

  const handleCardClick = (card) => {
    setSelectedCard(card);
  };

  const confirmCardDelete = (card) => {
    setCardToDelete(card);
    setIsConfirmPopupOpen(true);
  };

  //function for setting like to card
  const handleCardLike = (card) => {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    api.toggleLikeStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
      })
      .catch((err) => console.log(err));
  };

  //function for deleting card
  const handleCardDelete = (card) => {
    api
      .deleteCard(card._id)
      .then(() => {
        setCards((state) => state.filter((c) => c._id !== card._id));
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  };

  //function for editing user info
  const handleUpdateUser = (data) => {
    api
      .editUserInfo(data)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  };

  //function for edtiting user avatar
  const handleUpdateAvatar = (data) => {
    api
      .editAvatar(data)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  };

  //function for adding cards
  const handleAddCards = (data) => {
    api
      .addCard(data)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  };


  //function for closing popups
  const closeAllPopups = () => {
    setEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsConfirmPopupOpen(false);
    setSelectedCard(null);
    setCardToDelete(null);
  };

  // const handle

  return (
    <div className="page">
      <CurrentUserContext.Provider value={currentUser}>
        <Header />

        <Routes>
          <Route
          path="/"
          element={
            <ProtectedRoute
            component={Main}
            loggedIn={loggedIn}
            userData={userData}
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onEditAvatar={handleEditAvatarClick}
            onCardClick={handleCardClick}
            onCardLike={handleCardLike}
            onCardDelete={confirmCardDelete}
            cards={cards}
            />
          }
          />
          <Route
          path="/"
          element={<ProtectedRoute component={Footer} loggedIn={loggedIn}/>}
          />

          <Route path="/sign-in" element={<Login handleLogin={handleLogin}/>}
          />
          <Route path="/sign-up" element={<Register handleRegister={handleRegister}/>}
          />

        </Routes>

        {/* <Footer /> */}
        <EditProfilePopup
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        onUpdateUser={handleUpdateUser} />

        <AddPlacePopup
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
        onAddPlace={handleAddCards} />

        <EditAvatarPopup
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
        onUpdateUser={handleUpdateAvatar} />

        <ConfirmDeletePopup
        isOpen={isConfirmPopupOpen}
        onClose={closeAllPopups}
        onUpdateUser={handleUpdateAvatar}
        onConfirm={handleCardDelete}
        card={cardToDelete} />

        <ImagePopup
        card={selectedCard}
        onClose={closeAllPopups} />
      </CurrentUserContext.Provider>
    </div>
  );
};

export default App;
